"use strict";

// load configuration
let conf;
try {
	conf = require("../../build.user.conf");
} catch(e) {
	conf = require("../../build.conf");
}

// pull the process arguments we care about out
let args = [];

for (let i = 2; i < process.argv.length; i++) {
	let arg = process.argv[i];

	if (arg.startsWith("--")) {
		args = process.argv.slice(i);
		break;
	}
}

// black list: don't build these modules
// white list: only build these modules
// gray list: build these modules despite defaults
let params = {
	white: null,
	gray: null,
	black: null
};

let getValue = (name, base, core) => {
	let conf = core.conf;

	if (core.params[name] != null) {
		return core.params[name];
	}

	if (conf[name] != null) {
		return conf[name];
	}

	return base;
};

let core = {
	version: "2.0.0",

	modules: ["browser", "server", "styles", "static"],
	buildTasks: [],
	browserSync: null,

	conf: conf,
	params: params,

	get once() {
		return getValue("once", false, core);
	},

	get production() {
		return getValue("production", false, core);
	},

	get sourcemaps() {
		return getValue("sourcemaps", !core.production, core);
	},

	get minify() {
		return getValue("minify", !core.production, core);
	},

	load: (path) => {
		try {
			return require(path);
		} catch(e) {
			console.log(`Couldn't load module "${path}", try running "npm install"`);
			process.exit();
		}
	},

	getBuildPath: () => core.production ? core.conf.releasePath : core.conf.debugPath,

	getName: (transform) => {
		if (transform.name) {
			return transform.name;
		}

		return transform.type.charAt(0).toUpperCase() + transform.type.slice(1);
	},

	getTransforms: (type) => {
		let params = core.params;

		return core.conf.transforms.filter(transform => {
			let name = (transform.name || transform.type).toLowerCase();

			if (type != null && transform.type !== type) {
				return false;
			}

			if (params.black && params.black[name]) {
				return false;
			}

			if (params.white && !params.white[name]) {
				return false;
			}

			if (transform.disabled) {
				if (params.gray) {
					if (!params.gray[name]) {
						return false;
					}
				} else {
					return false;
				}
			}

			return true;
		});
	},

	onError: function(err) {
		require("gulp-notify").onError("Error: <%= error.message %>")(err);

		this.emit("end");
	}
};

{
	let isList = false;
	let listMode = "";

	let modes = {
		white: true,
		gray: true,
		black: true
	};

	let flags = {
		once: true,
		production: true,
		sourcemaps: true,
		minify: true
	};

	let aliases = {
		"only": "white",
		"except": "black"
	};

	let toBoolean = (v) => {
		v = v.toLowerCase();

		if (v === "true" || v === "yes") {
			return true;
		}

		if (v === "false" || v === "no") {
			return false;
		}

		return true;
	};

	for (let arg of args) {
		if (arg.startsWith("--")) {
			arg = arg.slice(2).toLowerCase();

			arg = aliases[arg] || arg;

			if (modes[arg]) {
				isList = true;
				listMode = arg;

				if (params[arg] == null) {
					params[arg] = {};
				}

				continue;
			}

			if (flags[arg]) {
				params[arg] = true;

				continue;
			}

			let setter = arg.match(/^([^=])+=(.+)/);

			if (setter) {
				let key = setter[1];
				let value = setter[2];

				if (flags[key]) {
					params[key] = toBoolean(value);
				}

				continue;
			}

			console.error(`Unknown command line option '${arg}'`);
			process.exit(1);
		} else {
			if (isList) {
				let list = arg.split(",");
				for (let e of list) {
					params[listMode][e.toLowerCase()] = true;
				}
			}
		}
	}
}

module.exports = core;