"use strict";

const core = require("./core");
const load = core.load;

console.log(`Basis v${core.version}`);
console.log(`Loading dependencies`);

const gulp = load("gulp");
const browserSync = load("browser-sync").create();

core.browserSync = browserSync;

for (let module of core.modules) {
	require("./" + module);
}

gulp.task("default", () => {
	if (core.watch) {
		if (core.conf.browserSync) {
			browserSync.init(core.conf.browserSync);
		}
	}

	return gulp.start(core.buildTasks);
});