"use strict";

let core = require("./core");
let load = core.load;

console.log(`Basis v${core.version}`);
console.log(`Loading dependencies`);

let gulp = load("gulp");
let browserSync = load("browser-sync").create();

core.browserSync = browserSync;

for (let module of core.modules) {
	require("./" + module);
}

console.log(`Building`);

gulp.task("once", () => {
	core.once = true;

	return gulp.start(core.buildTasks);
});

gulp.task("watch", () => {
	if (core.conf.proxy) {
		browserSync.init({
			proxy: core.conf.proxy
		});
	}
});

gulp.task("default", () => {
	let tasks = [];
	tasks.push(...core.buildTasks);
	tasks.push("watch");

	return gulp.start(tasks);
});