"use strict";

const core = require("../core");
const loader = core.getLoader();
const $ = loader.store;

const gulp = require("gulp");

loader.depend({
	path: "path",

	merge: "merge-stream",
	source: "vinyl-source-stream",
	buffer: "vinyl-buffer",

	browserify: "browserify",
	tsify: "tsify",
	watchify: "watchify",

	gutil: "gulp-util",
	sourcemaps: "gulp-sourcemaps",
	rename: "gulp-rename",
	uglify: "gulp-uglify"
});

core.buildTasks.push("build:browser");

const buildPipeline = pipeline => {
	let buildPath = core.out;

	let dest = $.path.join(buildPath, pipeline.dest);
	let ppath = $.path.parse(dest);
	let outDir = ppath.dir;

	let entries = [pipeline.source];

	if (pipeline.extraEntries) {
		entries.push(...pipeline.extraEntries);
	}

	let args = $.watchify.args;
	args.extensions = [".ts", ".js"];
	args.entries = entries;
	args.debug = true;

	if (pipeline.config && pipeline.config.rootDir) {
		args.paths = [pipeline.config.rootDir];
	}

	if (pipeline.config && pipeline.config.browserify) {
		Object.assign(args, pipeline.config.browserify);
	}

	let bundler = $.browserify(args);

	if (core.watch) {
		bundler = $.watchify(bundler);
	}

	bundler = bundler.plugin($.tsify, pipeline.config && pipeline.config.typescript);

	let rebundle = () => {
		$.gutil.log($.gutil.colors.green(core.getName(pipeline) + ": building..."));

		const handler = core.getErrorHandler(core.getName(pipeline));

		let stream = bundler.bundle()
			.on("error", handler)
			.pipe($.source(pipeline.source))
			.pipe($.buffer());

		if (core.sourcemaps) {
			stream = stream
				.pipe($.sourcemaps.init({ loadMaps: true }))
		}

		if (core.minify) {
			stream = stream
				.pipe($.uglify())
		}

		stream = stream
			.pipe($.rename(ppath.base));

		if (core.sourcemaps) {
			stream = stream
				.pipe($.sourcemaps.write("./"));
		}

		stream = stream
			.pipe(gulp.dest(outDir))
			.pipe(core.browserSync.stream())
			.pipe(core.getNotify(core.getName(pipeline) + ": done!"));

		return stream;
	}

	if (core.watch) {
		bundler.on("update", rebundle);
	}

	return rebundle();
};

gulp.task("build:browser", () => {
	loader.load();

	let merged = $.merge();

	for (let pipeline of core.getPipelines("browser")) {
		merged.add(buildPipeline(pipeline));
	}

	return merged;
});