"use strict";

const core = require("../core");
const loader = core.getLoader();
const $ = loader.store;

const gulp = require("gulp");
const merge = require("merge-stream");

loader.depend({
	path: "path",

	rename: "gulp-rename",
	gutil: "gulp-util"
});

core.buildTasks.push("build:static");

const buildPipeline = pipeline => {
	$.gutil.log($.gutil.colors.green(core.getName(pipeline) + ": building..."));

	let buildPath = core.out;

	let dest = $.path.join(buildPath, pipeline.dest);

	let stream = gulp.src(pipeline.source);

	if (pipeline.config && pipeline.config.rename) {
		stream = stream.pipe($.rename(pipeline.config.rename));
	}

	stream = stream.pipe(gulp.dest(dest))
		.pipe(core.browserSync.stream())
		.pipe(core.getNotify(core.getName(pipeline) + ": done!"));

	return stream;
};

gulp.task("build:static", () => {
	const pipelines = core.getPipelines("static");
	let merged = merge();

	if (pipelines.length > 0) {
		loader.load();
	}

	for (let pipeline of pipelines) {
		merged.add(buildPipeline(pipeline));

		if (core.watch) {
			gulp.watch(pipeline.source, e => {
				buildPipeline(pipeline);
			});
		}
	}

	return merged;
});